const ThemeClasses = ({ themes, themeSelected }) => {
  let theme = themes.find((th) => themeSelected === th.themeValue);

  return (
    <>
      <div className='containerFluid1 container1-lg'>
        <div className='mainTitle pb2'>
          <h5 className='fontStyle fsH3'>Font Family:</h5>
        </div>

        <div className='row alignCenter fontDefault mb1'>
          <div className='col3'>
            <h5 className='fsMD'>fontDefault:</h5>
          </div>

          <div className='col9'>
            <h6 className='fsSM'>{theme.themeClasses.fontDefault}</h6>
          </div>
        </div>

        <div className='row alignCenter fontDefault mb1'>
          <div className='col3'>
            <h5 className='fsMD'>fontDefault:</h5>
          </div>

          <div className='col9'>
            <h6 className='fsSM'>{theme.themeClasses.fontDefault}</h6>
          </div>
        </div>

        {/* <div className='row alignCenter fontStyle mb1'>
