import './styles/css/styles.css';
import { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import ThemesOptions from './components/ThemesOptions';

function App() {
  const [themeSelected, setThemeSelected] = useState('blue');

  const themesOptions = [
    { id: 1, themeName: 'Blue', themeValue: 'blue' },
    { id: 2, themeName: 'Orange', themeValue: 'orange' },
    { id: 3, themeName: 'Teal', themeValue: 'teal' },
  ];

  let themeValue = themesOptions.find(
    (to) => to.themeValue === themeSelected
  ).themeValue;

  const handleThemeChange = (e) => {
    const { name, value } = e.target;
    setThemeSelected(value);
  };

  return (
    <>
      <div data-theme={themeValue} className='app-wrapper'>
        <Header />

        <div className='main-wrapper'>
          <main>
            <section className='pb-2 ml-n border'>
              <h2 className='fontStyle fsH2'>Them Options</h2>
              <ThemesOptions
                themesOptions={themesOptions}
                themeSelected={themeSelected}
                handleThemeChange={handleThemeChange}
              />
            </section>
          </main>
        </div>

        <Footer />
      </div>
    </>
  );
}

export default App;
