const ThemesOptions = ({ themesOptions, themeSelected, handleThemeChange }) => {
  return (
    <>
      <div className='container-1'>
        <div className='main-title py-2'>
          <h6 className='fs-h3'>Themes Options</h6>
        </div>

        <div className='filters'>
          <select
            id='themes-options'
            name='theme-option'
            value={themeSelected}
            onChange={handleThemeChange}>
            {themesOptions.map((option) => (
              <option key={option.id} value={option.themeValue}>
                {option.themeName}
              </option>
            ))}
          </select>
        </div>
      </div>
    </>
  );
};

export default ThemesOptions;
