import './styles/css/styles.css';
import { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import ThemesOptions from './components/ThemesOptions';
import ThemeClasses from './components/ThemeClasses';

function App() {
  const [themeSelected, setThemeSelected] = useState('blue');

  const themes = [
    {
      id: 1,
      themeName: 'Blue',
      themeValue: 'blue',
      themeClasses: {
        id: 11,
        btnColor: '#4682b4',
        fontDefault: 'Montserrat, sans-serif',
        fontStyled: 'Ranchers, display',
        fontAccent: 'Licorice, handwriting',
      },
    },
    {
      id: 2,
      themeName: 'Orange',
      themeValue: 'orange',
      themeClasses: {
        id: 12,
        btnColor: '#fd7e14',
        fontDefault: 'Ranchers, display',
        fontStyle: 'Licorice, handwriting',
        fontAccent: 'Montserrat, sans-serif',
      },
    },
    {
      id: 3,
      themeName: 'Teal',
      themeValue: 'teal',
      themeClasses: {
        id: 13,
        btnColor: '#008080',
        fontDefault: 'Licorice, handwriting',
        fontStyle: 'Ranchers, display',
        fontAccent: 'Montserrat, sans-serif',
      },
    },
  ];

  console.log(themes);

  let themeValue = themes.find(
    (th) => th.themeValue === themeSelected
  ).themeValue;

  const handleThemeChange = (e) => {
    const { name, value } = e.target;
    setThemeSelected(value);
  };

  return (
    <>
      <div data-theme={themeValue} id='appWrapper' className='appWrapper'>
        <Header />

        <div className='mainWrapper'>
          <main>
            <section className='pb2 borderBottom'>
              <ThemesOptions
                themes={themes}
                handleThemeChange={handleThemeChange}
              />
            </section>

            <section className='py2 borderBottom'>
              <ThemeClasses
                themes={themes}
                themeSelected={themeSelected}
              />
            </section>
          </main>
        </div>

        <Footer />
      </div>
    </>
  );
}

export default App;
