import './styles/css/styles.css';
import { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import ThemesOptions from './components/ThemesOptions';
import ThemeHeadings from './components/ThemeHeadings';

function App() {
  const [themeSelected, setThemeSelected] = useState('blue');

  const themesOptions = [
    { id: 1, themeName: 'Blue', themeValue: 'blue', btnColor: '#4682b4', f },
    {
      id: 2,
      themeName: 'Orange',
      themeValue: 'orange',
      btnColor: '#fd7e14',
    },
    { id: 3, themeName: 'Teal', themeValue: 'teal', btnColor: '#008080' },
  ];

  let themeValue = themesOptions.find(
    (to) => to.themeValue === themeSelected
  ).themeValue;

  const handleThemeChange = (e) => {
    const { name, value } = e.target;
    setThemeSelected(value);
  };

  return (
    <>
      <div data-theme={themeValue} id='appWrapper' className='appWrapper'>
        <Header />

        <div className='mainWrapper'>
          <main>
            <section className='pb2 borderBottom'>
              <ThemesOptions
                themesOptions={themesOptions}
                themeSelected={themeSelected}
                handleThemeChange={handleThemeChange}
              />
            </section>
            <section className='py2 borderBottom'>
              <ThemeHeadings />
            </section>
          </main>
        </div>

        <Footer />
      </div>
    </>
  );
}

export default App;
