const ThemesOptions = ({ themesOptions, handleThemeChange }) => {
  return (
    <>
      <div className='containerFluid1 container1-lg'>
        <div className='mainTitle py2'>
          <h6 className='fontStyle fsH3'>Themes Options:</h6>
        </div>

        <div>
          <div className='buttons row gap1 justifyStart'>
            {themesOptions.map((themesOption) => (
              <>
                <div key={themesOption.id}>
                  <button
                    type='button'
                    onClick={handleThemeChange}
                    value={themesOption.themeValue}
                    className='btn btnMD light'
                    style={{
                      backgroundColor: themesOption.themeClasses.btnColor,
                    }}>
                    {themesOption.themeName}
                  </button>
                </div>

                <section className='py2 borderBottom'>

                </section>
              </>
            ))}
          </div>
        </div>

        {/* <div className='filters'>
