import './styles/css/styles.css';
import { useState } from 'react';
import Header from './components/Header';
import Footer from './components/Footer';
import ThemesOptions from './components/ThemesOptions';

function App() {
  const [themeSelected, setThemeSelected] = useState('orange');

  const [themesOptions, setThemesOptions] = useState(
    { id: 1, themeName: 'Blue', themeValue: 'blue' },
    { id: 2, themeName: 'Orange', themeValue: 'orange' }
  );

  const { themeValue } = themes;

  const handleThemeChange = (e) => {
    const { name, value } = e.target;
    console.log(name, value);
    setThemeSelected(value);
  };

  return (
    <>
      <div data-theme={themeValue} className='app-wrapper'>
        <Header />

        <div className='main-wrapper'>
          <main>
            <section className='pb-2'>
              <h2 className='fontStyle'>Them Options</h2>
              <ThemesOptions
                themesOptions={themesOptions}
                themeSelected={themeSelected}
                handleThemeChange={handleThemeChange}
              />
            </section>
          </main>
        </div>

        <Footer />
      </div>
    </>
  );
}

export default App;
