import React from 'react';

const Borders = () => {
  const bordersClasses = {
    id: 1100,
    borders: [
      { id: 1101, classTitle: 'border', classValue: 'border' },
      { id: 1102, classTitle: 'borderTop', classValue: 'borderTop' },
      { id: 1103, classTitle: 'borderLeft', classValue: 'borderLeft' },
      { id: 1104, classTitle: 'borderBottom', classValue: 'borderBottom' },
      { id: 1105, classTitle: 'borderRight', classValue: 'borderRight' },
    ],
    borderRadius: [
      {
        id: 1106,
        classTitle: 'brDef',
        classValue: 'brDef',
        value: '1px',
      },
      {
        id: 1107,
        classTitle: 'br1px',
        classValue: 'br1px',
        value: '1px',
      },
      {
        id: 1108,
        classTitle: 'br2px',
        classValue: 'br2px',
        value: '2px',
      },
      {
        id: 1109,
        classTitle: 'br2px',
        classValue: 'br2px',
        value: '2px',
      },
    ],
  };

  //   $brDefault: 1px;
  // $br1px: $brDefault;
  // $br2px: $brDefault * 2;
  // $br3px: $brDefault * 3;
  // $br4px: $brDefault * 4;
  // $br5px: $brDefault * 5;
  // $br10px: $brDefault * 10;
  // $br15px: $brDefault * 15;
  // $br20px: $brDefault * 20;
  // $br25px: $brDefault * 25;
  // $brRound: 50%;

  return (
    <>
      <div className='containerFluid1 container1-lg'>
        <div className='sectionTitle fontStyled primary'>
          <h5 className='fsH5 pb2'>Borders</h5>
        </div>

        <div className='row fontStyled fwBold primary mb1'>
          <div className='col5 col4-xs col3-sm'>Classes</div>
          <div className='col7 col6-xs col4-sm'>Appearances</div>
        </div>

        <div className='row alignCenter mb1'>
          <div className='col5 col4-xs col3-sm'>
            <p className='fsSM'>borderDef:</p>
          </div>

          <div className='col7 col6-xs col4-sm'>
            <div className='p1'>
              1px solid <span className='borderColor'> borderColor </span>
            </div>
          </div>
        </div>

        {bordersClasses.borders.map((bd) => (
          <div key={bd.id} className='row alignCenter mb1'>
            <div className='col5 col4-xs col3-sm'>
              <p className='fsSM'>.{bd.classValue}:</p>
            </div>

            <div className='col7 col4-xs col3-sm'>
              <div className={`textCenter p1 ${bd.classValue}`}>
                {bd.classTitle}
              </div>
            </div>
          </div>
        ))}

        <div className='sectionTitle fontStyled primary'>
          <h5 className='fsH5 pb2'>Border Radiuses</h5>
        </div>

        <div className='row fontStyled fwBold primary mb1'>
          <div className='col5 col4-xs col3-sm'>Classes</div>
          <div className='col7 col6-xs col4-sm'>Appearances</div>
        </div>

        <div className='row alignCenter mb1'>
          <div className='col5 col4-xs col3-sm'>
            <p className='fsSM'>borderDef:</p>
          </div>

          <div className='col7 col6-xs col4-sm'>
            <div className='p1'>
              1px solid <span className='borderColor'> borderColor </span>
            </div>
          </div>
        </div>

        {bordersClasses.borderRadius.map((br) => (
          <div key={br.id} className='row alignCenter mb1'>
            <div className='col5 col4-xs col3-sm'>
              <p className='fsSM'>.{br.classValue}:</p>
            </div>

            <div className='col7 col4-xs col3-sm'>
              <div className={`border textCenter p1 ${br.classValue}`}>
                {br.classTitle}
              </div>
            </div>
          </div>
        ))}
      </div>
    </>
  );
};

export default Borders;
