const Grid = () => {
  const grids = {
    id: 1200,
    gaps: [
      { id: 1201, classTitle: 'gap1', classValue: '0.5rem' },
      { id: 1202, classTitle: 'gap2', classValue: '1rem' },
      { id: 1203, classTitle: 'gap3', classValue: '1.5rem' },
    ],
  };

  //   $gridColumns: 12;
  // $gap: 0.5rem;
  // $gapSM: $gap;
  // $gapMD: $gap * 2;
  // $gapLG: $gap * 3;
  // $negGapSM: neg($gap) * 1;
  // $negGapMD: neg($gap) * 2;
  // $negGapLG: neg($gap) * 3;

  return (
    <>
      <div className='containerFluid1 container1-lg'>
        <div className='sectionTitle fontStyled primary'>
          <h5 className='fsH5 pb2'>Grids</h5>
        </div>

        <div className='row fontStyled fwBold primary mb1'>
          <div className='col3'>Titles</div>
          <div className='col9'>Values</div>
        </div>

        <div className='rowWrapper'>
          {grids.gaps.map((gap) => (
            <div key={gap.id} className='row alignCenter mb1'>
              <div className='col3 fwBold'>
                <p className='fsSM'>.{gap.classTitle}:</p>
              </div>
              <div className='col3'>
                <h1 className='fsSM fontDefault'>{gap.classValue}</h1>
              </div>
              <div className='col6'>
                <div className={`row ${gap.classTitle}`}>
                  <div className='border p1'>
                    <p className='border p1'>{gap.classTitle}</p>
                  </div>
                  <div className='border p1'>
                  <p className='border p1'>{gap.classTitle}</p>
                  </div>
                  <div className='border p1'>
                  <p className='border p1'>{gap.classTitle}</p>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default Grid;
